export class VCard {
    /**
     * @param { any } [suitOrCard]
     * @param { number | Card[] } [numberOrCards]
     * @param { string } [name]
     * @param { string } [nature]
     * @param { Player | false } [owner]
     */
    constructor(suitOrCard?: any, numberOrCards?: number | Card[], name?: string, nature?: string, owner?: Player | false);
    /**
     * @type {string}
     */
    suit: string;
    /**
     * @type {number}
     */
    number: number;
    /**
     * @type {string}
     */
    name: string;
    /**
     * @type {string}
     */
    nature: string;
    color: string;
    /**
     * @type { boolean }
     */
    isCard: boolean;
    cardid: any;
    wunature: any;
    /**
     * @type {Record<string, any>}
     */
    storage: Record<string, any>;
    cards: Card[];
    sameSuitAs(card: any): boolean;
    differentSuitFrom(card: any): boolean;
    sameNumberAs(card: any): boolean;
    differentNumberFrom(card: any): boolean;
    sameNameAs(card: any): boolean;
    differentNameFrom(card: any): boolean;
    /**
     * @param { Player } player
     */
    hasNature(nature: any, player: Player): boolean;
    /**
     * 返回一个键值，用于在缓存中作为键名。
     * @param { boolean } [similar] true伪equals, false统一前缀
     * @returns {string} cacheKey
     */
    getCacheKey(similar?: boolean): string;
    hasGaintag(tag: any): any;
    initID(): void;
    vcardID: string;
}
